INSERT INTO `email_templates` (`id`, `email_subject`, `email_body`) VALUES
(1, '{ENGINE_NAME} Advertiser Registration', 'Hi {USERNAME}\r\n\r\nThank you for registering at {ENGINE_NAME}.\r\nYour advertiser account is active.\r\nYour login name is given below.\r\nUsername:{USERNAME}\r\n\r\n<a href="{ADV_LOGIN_PATH}">Click here to login</a>\r\n\r\nRegards\r\n{ENGINE_NAME}'),
(2, '{ENGINE_NAME}  Publisher Registration', 'Hi {USERNAME}\r\n\r\nThank you for registering at {ENGINE_NAME}.\r\n\r\nYour login name is given below.\r\nUsername:{USERNAME}\r\n\r\nYou account is pending approval.\r\nAdmin will check your request soon.\r\n\r\n\r\nRegards\r\n{ENGINE_NAME}'),
(3, '{ENGINE_NAME} Publisher Account Approval', 'Hi {USERNAME}\r\n\r\nYour request for {ENGINE_NAME} publisher account has been approved.\r\n<a href="{PUB_LOGIN_PATH}">Click here to login</a>\r\n\r\n\r\nRegards\r\n{ENGINE_NAME}'),
(4, '{ENGINE_NAME} Publisher Account Rejected', 'Hi  {USERNAME}\r\n\r\nWe are temporarily disabling your publisher application due to unavoidable reasons. \r\n\r\nRegards\r\n{ENGINE_NAME}'),
(5, '{ENGINE_NAME}  publisher account status', 'Hi  {USERNAME}\r\n\r\nYour {ENGINE_NAME} publisher account has been blocked due to one or more of the following reasons.\r\n\r\n1)Placing ads aginst our terms.\r\n\r\n2)Fraud clicking activity.\r\n\r\nRegards\r\n{ENGINE_NAME}'),
(6, '{ENGINE_NAME} publisher account activated', 'Hi {USERNAME}\r\n\r\nYour {ENGINE_NAME} publisher account is active now.\r\n<a href="{PUB_LOGIN_PATH}">Click here to login</a>\r\n\r\n\r\nRegards\r\n{ENGINE_NAME}'),
(7, '{ENGINE_NAME} advertiser account blocked', 'Hi {USERNAME}\r\n\r\nYour {ENGINE_NAME} advertiser account has been blocked due to one or more of the following reasons.\r\n\r\n1)Posting spam ads.\r\n\r\n2)Posting ads against our terms.\r\n\r\nRegards\r\n{ENGINE_NAME}'),
(8, '{ENGINE_NAME} advertiser account activated', 'Hi {USERNAME}\r\n\r\nYour {ENGINE_NAME}  advertiser account is active now.\r\n<a href="{ADV_LOGIN_PATH}">Click here to login</a>\r\n\r\n\r\nRegards\r\n{ENGINE_NAME}'),
(9, '{ENGINE_NAME} ad activated', 'Hi {USERNAME}\r\n\r\nOne of your ad has been activated.\r\n\r\n{AD_ID}\r\n\r\nRegards\r\n{ENGINE_NAME}'),
(10, '{ENGINE_NAME} ad blocked', 'Hi {USERNAME}\r\n\r\nOne of your ad has been blocked.\r\n\r\n{AD_ID}\r\n\r\nRegards\r\n{ENGINE_NAME}'),
(11, '{ENGINE_NAME} ad deleted', 'Hi {USERNAME}\r\n\r\nOne of your ads has been deleted because of one or more of the following reasons\r\n\r\n1)Your ad is a spam ad.\r\n\r\n2)Your ad is against our terms.\r\n\r\nRegards\r\n{ENGINE_NAME}'),
(12, '{ENGINE_NAME} Fund Recieved', 'Hi {USERNAME}\r\n\r\n\r\nYou have successfully added funds to {ENGINE_NAME} advertiser account\r\nPayment Mode   : {PAYMODE} \r\nPayment Amount : {PAYMENT_AMOUNT}\r\nBonus Amount  : {BONOUS_AMOUNT}\r\n\r\n\r\nRegards\r\n{ENGINE_NAME}'),
(13, '{ENGINE_NAME} warning', 'Hi  {USERNAME}\r\n\r\nWe have identified that you are doing fraud activity with your publisher account.\r\nYour fraud clicks end earnings have been deleted.\r\nThis is your first and last warning.\r\nIf you further attempt fraud clicks, your account will be blocked.\r\n\r\nRegards\r\n{ENGINE_NAME}'),
(14, '{ENGINE_NAME}  publisher account status', 'Hi  {USERNAME}\r\n\r\nWe have identified that you are doing fraud activity with your publisher account.\r\nYour fraud clicks end earnings have been deleted and your account has been blocked.\r\n\r\nRegards\r\n{ENGINE_NAME}'),
(15, '{ENGINE_NAME} Account balance running low', 'Hi  {USERNAME}\r\n\r\nYou account balance is running low.\r\nPlease add sufficient funds to your account immediately to ensure that your ads won''t stop showing. \r\n\r\nRegards\r\n{ENGINE_NAME}'),
(16, '{ENGINE_NAME}  Ad monthly budget crossed', 'Hi  {USERNAME}\r\n\r\nOne of your ads has crossed the monthly budget limit.\r\n\r\nAd Id: {AD_ID}\r\n\r\nPlease consider increasing the budget for the same.\r\n\r\nRegards\r\n{ENGINE_NAME}'),
(17, '{ENGINE_NAME} Advertiser Account Approval', 'Hi {USERNAME}\r\n\r\nYour request for {ENGINE_NAME} advertiser account has been approved.\r\n<a href="{ADV_LOGIN_PATH}">Click here to login</a>\r\n\r\n\r\nRegards\r\n{ENGINE_NAME}'),
(18, '{ENGINE_NAME} Advertiser Account Rejected', 'Hi {USERNAME}\r\n\r\nWe are temporarily disabling your advertiser application due to unavoidable reasons. \r\n\r\nRegards\r\n{ENGINE_NAME}'),
(19, '{ENGINE_NAME} Advertiser Email Confirmation', 'Hi {USERNAME}\r\n\r\nYour request for {ENGINE_NAME} advertiser account is under verification.\r\n<a href="{ADV_CONFIRM_PATH}">Click here</a>  to confirm your email.\r\n\r\n\r\nRegards\r\n{ENGINE_NAME}'),
(20, '{ENGINE_NAME} Publisher Email Confirmation', 'Hi {USERNAME}\r\n\r\nYour request for {ENGINE_NAME} publisher account is under verification.\r\n<a href="{PUB_CONFIRM_PATH}">Click here</a>  to confirm your email.\r\n\r\n\r\nRegards\r\n{ENGINE_NAME}'),
(21, '{ENGINE_NAME} advertiser account login info', 'Hello, \r\n\r\nYou had requested for resetting your password. Your new login credentials are given below.\r\n\r\nUsername : {USERNAME} \r\n\r\nNew Password : {PASSWORD} \r\n\r\nPlease login and change the the temporary password \r\n\r\nThanks\r\n{ENGINE_NAME}'),
(22, '{ENGINE_NAME} publisher account login info', 'Hello, \r\n\r\nYou had requested for resetting your password. Your new login credentials are given below.\r\n\r\nUsername : {USERNAME} \r\n\r\nNew Password : {PASSWORD} \r\n\r\nPlease login and change the the temporary password \r\n\r\nThanks\r\n{ENGINE_NAME}'),
(23, '{ENGINE_NAME} Account login info', 'Hello, \r\n\r\nYou had requested for resetting your password. Your new login credentials are given below.\r\n\r\nUsername  : {USERNAME} \r\n\r\nNew Password : {PASSWORD} \r\n\r\nPlease login and change the the temporary password \r\n\r\nThanks\r\n{ENGINE_NAME}'),
(24, '{ENGINE_NAME} Registration', 'Hi {USERNAME}\r\n\r\nThank you for registering at {ENGINE_NAME}.\r\n\r\nYour login name is given below.\r\nUsername:{USERNAME}\r\n\r\nYour advertiser account is {ADV_STATUS} and publisher account is {PUB_STATUS}.\r\nAdmin will check your request soon.\r\n\r\n\r\nRegards\r\n{ENGINE_NAME}'),
(25, '{ENGINE_NAME} Email Confirmation', 'Hi {USERNAME}\r\n\r\nYour request for {ENGINE_NAME} account is under verification.\r\n\r\n<a href="{CONFIRM_PATH}">Click here</a>  to confirm your email.\r\n\r\n\r\nRegards\r\n{ENGINE_NAME}'),
(26, '{ENGINE_NAME}  Account blocked', 'Hi {USERNAME}\r\n\r\nYour {ENGINE_NAME} advertiser and publisher accounts have been blocked .\r\n\r\nRegards\r\n{ENGINE_NAME}'),
(27, '{ENGINE_NAME} Account activated', 'Hi {USERNAME}\r\n\r\nYour {ENGINE_NAME} advertiser and publisher accounts are active now.\r\n<a href="{LOGIN_PATH}">Click here to login</a>\r\n\r\n\r\nRegards\r\n{ENGINE_NAME}'),
(28, '{ENGINE_NAME} Account Rejected', 'Hi  {USERNAME}\r\n\r\nWe are temporarily disabling your advertiser and publisher applications due to unavoidable reasons. \r\n\r\nRegards\r\n{ENGINE_NAME}'),
(29, '{ENGINE_NAME} Account Approval', 'Hi {USERNAME}\r\n\r\nYour request for {ENGINE_NAME} advertiser and publisher accounts has been approved.\r\n<a href="{LOGIN_PATH}">Click here to login</a>\r\n\r\n\r\nRegards\r\n{ENGINE_NAME}'),
(30, '{ENGINE_NAME}  Change status', 'Hi {USERNAME}\r\n\r\nYour advertiser account has been {ADV_STATUS} and publisher account has been {PUB_STATUS}.\r\n<a href="{LOGIN_PATH}">Click here to login</a>\r\n\r\nRegards\r\n{ENGINE_NAME}');
